$(document).ready(function () {
  $('.luceneapi-dym-suggestion').live('click', function(e){
    e.preventDefault();
    Drupal.search.start($(this).text());
    $('#search_input').val($(this).text());
  });
  
  Drupal.search.init($('#search_input'), $('#search_clear'));
});

(function() {
  var badKeys = {37:1, 38:1, 39:1, 40:1, 16:1, 18:1, 91:1, 9:1, 17:1, 27:1, 13:1},
      cache= {},
      input_el = null,
      clear_el = null,
      search_div = null,
      interval = null,
      counter = 0;
  
  Drupal.search = {
    init: function(ie, ce) {
      input_el = ie;
      clear_el = ce;

      // register keyups
      input_el.keyup(function (e) {
        counter = 0;

        if (e.keyCode == 27) {
          Drupal.search.clear(true);
        }
        else if (e.keyCode == 8 && input_el.val() == '') {
          clear_el.css('display', 'none');
        }
        else if (e.keyCode == 13) {
          clearInterval(interval);
          interval = false;
          Drupal.search.set_container();
          clear_el.addClass('loading');
          location.hash = 'search' + '/' + encodeURI(input_el.val());
        }
        
        if (!badKeys[e.keyCode]) {
          Drupal.search.check();
        }
      });
      
      clear_el.click(Drupal.search.clear);
    },

    check: function() {
      if (input_el.val().length > 0) {
        clear_el.css('display', 'block');
      }
      else if ($('#search').length > 0) {
        Drupal.search.clear();
      }
      
      if (input_el.val().replace(/^\s+|\s+$/g, '').length > 2 && !interval) {
        Drupal.search.set_interval();
      }
    },

    set_interval: function() {
        interval = setInterval(function () {
          counter += 1;

          if (counter > 35 && input_el.val().length > 2) {
            clearInterval(interval);
            interval = false;

            Drupal.search.set_container();
            clear_el.addClass('loading');

            // start search !!
            location.hash = 'search' + '/' + encodeURI(input_el.val());
          }
        }, 20);
    },

    set_container: function() {
      if ($('#search').length == 0) {
        search_div = document.createElement('div');
        search_div.id = 'search';
        $(search_div).css('opacity', 0);
        $(document.body).append(search_div);
      }

      search_div.isFull = false;
      search_div.isShown = false;
    },

    start: function(term) {
      Drupal.search.set_container();
      
      $('#content').ani({'opacity' : 0}, 400, function () {
        $('#content').css('display', 'none');
        search_div.isShown = true;
        if (search_div.isFull) {
          Drupal.search.show();
        }
      });

      if (typeof(is_front) != 'undefined') {
        $('#breadcrumb').fadeIn();
      }

      $('#breadcrumb li').not(':first-child').css({opacity: 0, display: 'none'});
      $('#breadcrumb').append('<li class="removeBread"><span class="white"><a href="#">Suche</a></span></li><li class="removeBread"><span class="white"><a href="#">' + term + '</a></span></li>');
      
      if (cache[term]) {
        search_div.isFull = true;
        search_div.innerHTML = cache[term];
      }
      else {
        $.ajax({
          url: Drupal.settings.basePath + 'hfkweb_search/get',
          data: {'term': term},
          success: function (data) {
            search_div.innerHTML = data;
            cache[term] = data;
            search_div.isFull = true;
            if (search_div.isShown) {
              Drupal.search.show();
            }
          }
        });
      }
    },

    show: function() {
      clear_el.removeClass('loading');
      $('#search').ani({'opacity' : 1}, 400);
      //async load of person thumbnails
      $('.resultprofilethumbnail').each(function(e) {
        this.src = Drupal.settings.basePath + $(this).attr('rel');
      });
    },

    clear: function(e) {
      input_el.val('');
      
      if (typeof is_front != 'undefined') {
        $('#breadcrumb').css('display', 'none');
      }

      $('#breadcrumb li').css('display', 'block');
      $('.removeBread').remove();
      
      if ($.browser.msie) {
        $('#breadcrumb li').ani({'opacity': 1}, 200);
      }
      else {
        $('#breadcrumb li').ani({'opacity': .6}, 200);
      }

      $('#search_clear').css('display', 'none');
      $('#search').remove();

      $('#content').css('display', 'block')
                   .ani({'opacity': 1});

      if (e) {
        location.hash = '';
      }

      if (!interval) {
        clearInterval(interval);
        interval = false;
      }
    }
  }
})();;
