Drupal.history = function(hash) {
  if (hash != '') {
    var url =  hash.split('/');
    switch (url[0]) {
      case 'search':
        Drupal.search.start(url[1]);
        $('#search_input').val(url[1]);
        $('#search_clear').css('display', 'block');
        break;
      case 'showgroup':
        more_navigation();
        break;
      case 'course_item':
        $('.course_item.active').removeClass('active');
        $('#'+url[1]).addClass('active');
        break;
      case 'filter':
        Drupal.projects.init(url[1], url[2], url[3], url[4], url[5]);
        break;
    }
  }
}

$(document).ready(function() {

  // init history
  $.history.init(Drupal.history);

  // trigger search on tag click
  $('.tags a').click(function(e) {
    e.preventDefault();
    $('#search_input').val(this.innerHTML)
                      .focus()
                      .keyup();
  });

  // Load More Navigation on the personen site
  if (location.pathname.slice(location.pathname.length-7, location.pathname.length) == 'persons') {
    //async load of person thumbnails
    $('.person').hover(function() {
      var el = $(this).children('.personthumb').children('.resultprofilethumbnail')[0];
      if (el) {
        el.src = Drupal.settings.basePath + $(el).attr('rel');
      }
    });

    //More Navigation Eventlistner
    $('.more_navigation-emphases select').change(function(e) {
      location.hash = this.value;
    });

    more_navigation();
    topnav();
    $(document).scroll(topnav);
  }

  // tricky ie fix
  if ($.browser.msie) {
    $('li:last-child').addClass('last-child');
  }

  $('#event-toggle-nav').click(start_bg_slide);

  var keys = [],
      konami = "38,38,40,40,37,39,37,39,66,65";
  $(document).keydown(function(e) {
    keys.push(e.keyCode);
    if (keys.toString().indexOf(konami) >= 0) {
      $('*').ani({padding: 2000}, 9000);
      keys = [];
    }
  });

  if (!Drupal.settings.projects) {
    $(window).resize(function() {
      resize_background();
    });
  }

  $('.hfk-js-link').click(function(e) {
      // Prevent all default events.
    e.preventDefault();
    var link = this;
    // Call js function - has to be defined globally.
    var js_func = $(this).attr('data-function');
    eval(js_func);
  });
});

function start_bg_slide() {
  // start slide
  if (!$(this).hasClass('active')) {
    hide_navigation(function() {
      $('#event-toggle-nav').addClass('active');
    });

    $('#content').hid(1200);
    $('#breadcrumb').hid(1200, function() {
      var p = '<div style="background-image: url(' + Drupal.settings.filedirPath + '/imagecache/' + get_bg_preset() + '/',
          d = {};

      $.each(Drupal.settings.slideShow, function(i, e) {
        d['#bgs-'+ i] = {
          id: '#bgs-'+ i,
          data: p + e.replace(/sites\/default\/files/, '') + ')"></div>'
        };
      });

      $('body').append('<div id="bg_slide"></div>');

      Slide.init(
        $('#bg_slide'), {
        data: d,
        play: true,
        speed: 4500,
        resize: false
      });

      $('#bg_slide').ani({opacity: 1}, 400);
    });
  }
  else {
    Slide.instance.bg_slide.destroy();
    show_navigation(function() {
      $('#event-toggle-nav').removeClass('active');
    });
    $('#bg_slide').ani({'opacity': 0}, 400, function() {
      $('#bg_slide').remove();
      $('#content').sho(1200);
      $('#breadcrumb').sho(1200);
    });
  }
}

