function more_navigation() {
  var hash = location.hash.split("/"),
      type = 'group';

  $('.person').removeClass('hidden');
  $('.more_navigation-groups').addClass('hidden');
  $('.more_navigation-groups a').removeClass('active');
  $('.more_navigation-maingroup a').removeClass('active');

  // Extract current filters.
  $available_filters = {
    showgroup: [],
    emphasis: [],
    person_type: []
  };
  var current_filter = null;
  for (var i = 0; i < hash.length; i++) {
    var key = hash[i].replace(/#/g, '');
    if (typeof $available_filters[key] !== 'undefined') {
      current_filter = key;
      continue;
    }
    if (current_filter != null && (typeof $available_filters[current_filter] !== 'undefined')) {
      $available_filters[current_filter].push(hash[i]);
    }
  }

  $('.more_navigation-groups a')
    .not('.person-filter-processed')
    .addClass('person-filter-processed')
    .click(function() {
      // Update all option values with a variable in its path.
      updateOptionValues($available_filters);
    });

  if (hash[0] == '#showgroup') {
    // Update all option values with a variable in its path.
    updateOptionValues($available_filters);
    if (hash[1] == 'all') {
      $('.more_navigation-maingroup a.filter-no').addClass('active');
    }
    else {
      $('.person').addClass('hidden');
      $('.more_navigation-emphases option').removeAttr('selected');
      $('.more_navigation-emphases option:first-child').attr('selected', 'selected');
      for (var i = 1; i < hash.length; i++) {
        if (hash[i] == 'emphasis') {
          type = 'emphasis';
          $('.more_navigation-groups a.group-' + hash[i + 1]).addClass('active');
          $('.more_navigation-groups a.group-' + hash[i + 1]).addClass('active');
          $('.more_navigation-emphases option').removeAttr('selected');
          $('.more_navigation-emphases option.emphasis-group-' + hash[i + 1]).attr('selected', 'selected');
          break;
        }
        if (hash[i] == 'person_type') {
          type = 'person_type';
          $('.more_navigation-emphases option').removeAttr('selected');
          $('.more_navigation-emphases option.person_type-' + hash[i + 1]).attr('selected', 'selected');
          break;
        }

        type = 'group';
        $('.more_navigation-groups.group-' + hash[i]).removeClass('hidden');
        $('.more_navigation-groups a.group-' + hash[i]).addClass('active');
        $('.more_navigation-maingroup a.group-' + hash[i]).addClass('active');
      }

      var group_class = '';
      if ($available_filters['showgroup'].length > 0) {
        group_class = '.group-' + $available_filters['showgroup'][$available_filters['showgroup'].length - 1];
      }
      $('.person.' + type + '-' + hash[hash.length - 1] + group_class).removeClass('hidden');
    }
  }

  $('h2.subtitle').each(function(){
    var subtitle = this;
    $(this).addClass('hidden');
    $(this).next('.group').children('div').not('.hidden').each(function(){
      $(subtitle).removeClass('hidden');
    });
  });

}

/**
 * Helper function to update values of select options based on current filters.
 *
 * @param array $available_filters
 *   List of available filters.
 */
function updateOptionValues($available_filters) {
  // Alter filter value options based on current filters.
  $('.more_navigation-groups .group-filter option').each(function() {
    $option = $(this);
    if (typeof $option.attr('data') === 'undefined') {
      // Save original value including dynamic variables.
      $option.attr('data', $option.val());
    }
    var option_value = $option.attr('data');
    $.each($available_filters, function(filter_name, filter_values) {
      if (filter_values.length > 0) {
        var matcher = new RegExp('{\\$' + filter_name + '}', 'gi');
        if (matcher.test(option_value)) {
          // Replace variable in value.
          $option.val(option_value.replace(matcher, filter_values.join('/')));
        }
      }
    });
  });
}

/**
 * Write decoded clickable email address to the page.
 */
function mail(domain, name, string) {
  var email = name + '@' + domain;
  email = email.replace(/[a-zA-Z]/g, function(c) {
    return String.fromCharCode((c <= "Z" ? 90 : 122) >= (c = c.charCodeAt(0) + 13) ? c : c - 26);
  });
  if(!string) {
    string = email;
  }
  document.write('<a href=\"mailto:' + email + '\">' + string + '</a>');
}

/**
 * Helper function to simulate a mailto-link for obfuscated emails.
 *
 * @param string domain
 *   The rot13-encoded domain of the email-address.
 * @param string name
 *   The rot13-encoded name of the email-address.
 */
function mailto(domain, name) {
  var string = name + '@' + domain;
  email = string.replace(/[a-zA-Z]/g, function(c) {
    return String.fromCharCode((c <= "Z" ? 90 : 122) >= (c = c.charCodeAt(0) + 13) ? c : c - 26);
  });
  location.href = 'mailto:' + email;

  return FALSE;
}

function topnav () {
  //fix topnavon top
  $('.topnav-parent').each(function(index) {
      if ($(this).offset().top < $(document).scrollTop() && $(this).height()+this.initTopPos+$(this).children('.topnav').height()+30 > $(document).scrollTop()) {
        $(this).children('.topnav').css({'position':'fixed', 'left':'0px', 'top':'0'});
      } else if ($(this).offset().top < $(document).scrollTop() && $(this).height()+this.initTopPos+$(this).children('.topnav').height()+30 <= $(document).scrollTop()) {
        $(this).children('.topnav').css({'position':'absolute', 'left':'-20px', 'top':$(this).height()+$(this).children('.topnav').height()+30});
      } else {
        if (!this.initTopPos) {
          this.initTopPos = $(this).children('.topnav').offset().top;
        }
        $(this).children('.topnav').css({'position':'absolute', 'left':'-20px', 'top':'0'});
      }
  });
}

