Drupal.overlay = function(id, callback) {
  $('body').append('<div id="'+ id +'"></div>');
  var $over = $('#' + id);

  $(document).keydown(function(e) {
    if (e.keyCode == 27) {
      remove_overlay();
    }
  });

  $over.click(function(e) {
    if (e.target.id == id) {
      remove_overlay();
    }
  });

  function remove_overlay() {
    $over.ani({opacity: 0}, 300, function() {
      $over.remove();
      if (callback) {
        callback();
      }
    });
  }

  return $over;
}
